using System;using System.Collections.Generic;using System.Xml;using System.Xml.Serialization;namespace BLHXML{	[XmlRoot(ElementName="breakfast_menu")]	public class Breakfast_menu	{		[XmlElement(ElementName="food")]		public List<Food> ListaElementoFood { get; set; }	}	[XmlRoot(ElementName="food")]	public class Food	{		[XmlElement(ElementName = "name")]		public Name Name { get; set; }		[XmlElement(ElementName = "price")]		public string Price { get; set; }		[XmlElement(ElementName = "description")]		public string Description { get; set; }		[XmlElement(ElementName = "calories")]		public decimal Calories { get; set; }	}	[XmlRoot(ElementName="name")]	public class Name	{		[XmlAttribute(AttributeName="id")]		public bool Id { get; set; }	}}